function watchForWidgetMaxWidthCorrection(a){var b=$("#"+a),c=b.parent().parent();$.inArray("ui-resizable",c.attr("class").split(/\s+/))?c.on("resize",function(a,d){var e=c.width(),f=c.height();b.css({"max-width":e-25+"px","max-height":f-83+"px"})}):console.log("widgetMaxWidthCorrection: component with id="+a+" is not grandchild of landing page card (having 'ui-resizable' class), please check DOM tree")}